<?php$route = '/services/:service_id/eligibility/';$app->post($route, function ($service_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$clean = filter_input_array(INPUT_POST,$_GET);	$service_id = mysql_real_escape_string($service_id);		if(isset($clean['eligibility']))		{		$eligibility_id = getGUID();				$eligibility = $clean['eligibility'];		$query = "INSERT INTO eligibility(";					$query .= "id,";		$query .= "service_id,";		$query .= "eligibility";		$query .= ") VALUES(";				$query .= "'" . mysql_real_escape_string($eligibility_id) . "',";		$query .= "'" . mysql_real_escape_string($service_id) . "',";		$query .= "'" . mysql_real_escape_string($eligibility) . "'";		$query .= ")";		//echo $query;		mysql_query($query) or die('Query failed: ' . mysql_error());		$F = array();			$F['id'] = $eligibility_id;		$F['service_id'] = $service_id;		$F['eligibility'] = $eligibility;			$ReturnObject = $F;		}			$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>