<?php$route = '/services/:service_id/fees/:fee_id/';$app->get($route, function ($service_id,$fee_id)  use ($app){	$ReturnObject = array();	$service_id = mysql_real_escape_string($service_id);	$fee_id = mysql_real_escape_string($fee_id);	$Query = "SELECT * FROM fees WHERE id = '" . $fee_id . "' AND service_id = '" . $service_id . "'";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$fee = $Database['fee'];		$F = array();			$F['id'] = $fee_id;		$F['service_id'] = $service_id;		$F['fee'] = $fee;						$ReturnObject = $F;		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>