<?php$route = '/services/:service_id/fees/:feed_id/';$app->put($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['fee'])){ $fee = $_GET['fee']; } else { $fee = '';}	$Query = "SELECT * FROM fee WHERE slug = '" . $slug . "'";	$Database = mysql_query($Query) or die('Query failed: ' . mysql_error());	if($Database && mysql_num_rows($Database))		{		$query = "UPDATE fee SET";		if(isset($service_id))			{			$query .= "service_id='" . mysql_real_escape_string($service_id) . "'";			}		if(isset($feed_id))			{			$query .= "feed_id='" . mysql_real_escape_string($feed_id) . "'";			}		if(isset($fee))			{			$query .= "fee='" . mysql_real_escape_string($fee) . "'";			}		$query .= " WHERE slug = '" . $slug . "'";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Fee Updated!";		$ReturnObject['slug'] = $slug;		}	$api->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>