<?php$route = '/services/:service_id/fees/';$app->post($route, function ($service_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$clean = filter_input_array(INPUT_POST,$_GET);	$service_id = mysql_real_escape_string($service_id);		if(isset($clean['fee']))		{		$fee_id = getGUID();				$fee = $clean['fee'];		$query = "INSERT INTO fees(";					$query .= "id,";		$query .= "service_id,";		$query .= "fee";		$query .= ") VALUES(";				$query .= "'" . mysql_real_escape_string($fee_id) . "',";		$query .= "'" . mysql_real_escape_string($service_id) . "',";		$query .= "'" . mysql_real_escape_string($fee) . "'";		$query .= ")";		//echo $query;		mysql_query($query) or die('Query failed: ' . mysql_error());		$F = array();			$F['id'] = $fee_id;		$F['service_id'] = $service_id;		$F['fee'] = $fee;			$ReturnObject = $F;		}			$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>