<?php$route = '/services/:service_id/funding/:funding_id/';$app->put($route, function ($service_id,$funding_id)  use ($app){	$request = $app->request();	$_GET = $request->params();			$service_id = mysql_real_escape_string($service_id);	$funding_id = mysql_real_escape_string($funding_id);	$source = mysql_real_escape_string($_GET['source']);	$query = "UPDATE funding SET ";	$query .= "service_id='" . mysql_real_escape_string($service_id) . "'";	$query .= ",id='" . mysql_real_escape_string($funding_id) . "'";	$query .= ",source='" . mysql_real_escape_string($source) . "'";	$query .= " WHERE id = '" . $funding_id . "'";	mysql_query($query) or die('Query failed: ' . mysql_error());	$F = array();		$F['id'] = $funding_id;	$F['service_id'] = $service_id;	$F['source'] = $source;			$ReturnObject = $F;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>