<?php$route = '/services/:service_id/holiday_schedule/:holiday_schedule_id/';$app->put($route, function ($service_id,$holiday_schedule_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$service_id = mysql_real_escape_string($service_id);	$holiday_schedule_id = mysql_real_escape_string($holiday_schedule_id);		$closed = mysql_real_escape_string($_GET['closed']);	$opens_at = mysql_real_escape_string($_GET['opens_at']);	$closes_at = mysql_real_escape_string($_GET['closes_at']);	$start_date = mysql_real_escape_string($_GET['start_date']);	$end_date = mysql_real_escape_string($_GET['end_date']);	$query = "UPDATE holiday_schedule SET ";	$query .= "service_id='" . mysql_real_escape_string($service_id) . "'";	$query .= ",closed='" . mysql_real_escape_string($closed) . "'";	$query .= ",opens_at='" . mysql_real_escape_string($opens_at) . "'";	$query .= ",closes_at='" . mysql_real_escape_string($closes_at) . "'";	$query .= ",start_date='" . mysql_real_escape_string($start_date) . "'";	$query .= ",end_date='" . mysql_real_escape_string($end_date) . "'";	$query .= " WHERE id = '" . $holiday_schedule_id . "'";	//echo $query;	mysql_query($query) or die('Query failed: ' . mysql_error());	$F = array();	$F['service_id'] = $service_id;	$F['id'] = $holiday_schedule_id;	$F['closed'] = $closed;	$F['opens_at'] = $opens_at;	$F['closes_at'] = $closes_at;	$F['start_date'] = $start_date;	$F['end_date'] = $end_date;		$ReturnObject = $F;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>