<?php$route = '/services/:service_id/interpretation-services/:interpretation_service_id/';$app->get($route, function ($service_id,$interpretation_services_id)  use ($app){	$ReturnObject = array();	$service_id = mysql_real_escape_string($service_id);	$interpretation_services_id = mysql_real_escape_string($interpretation_services_id);	$Query = "SELECT * FROM interpretation_services WHERE id = '" . $interpretation_services_id . "' AND service_id = '" . $service_id . "'";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$language = $Database['language'];		$F = array();			$F['id'] = $interpretation_services_id;		$F['service_id'] = $service_id;		$F['language'] = $language;						$ReturnObject = $F;		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>