<?php$route = '/services/:service_id/interpretation-services/:interpretation_service_id/';$app->put($route, function ($service_id,$interpretation_services_id)  use ($app){	$request = $app->request();	$_GET = $request->params();			$service_id = mysql_real_escape_string($service_id);	$interpretation_services_id = mysql_real_escape_string($interpretation_services_id);	$language = mysql_real_escape_string($_GET['language']);	$query = "UPDATE interpretation_services SET ";	$query .= "service_id='" . mysql_real_escape_string($service_id) . "'";	$query .= ",id='" . mysql_real_escape_string($interpretation_services_id) . "'";	$query .= ",language='" . mysql_real_escape_string($language) . "'";	$query .= " WHERE id = '" . $interpretation_services_id . "'";	mysql_query($query) or die('Query failed: ' . mysql_error());	$F = array();		$F['id'] = $interpretation_services_id;	$F['service_id'] = $service_id;	$F['language'] = $language;			$ReturnObject = $F;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>