<?php$route = '/services/:service_id/languages/:language_id/';$app->get($route, function ($service_id,$language_id)  use ($app){	$ReturnObject = array();	$service_id = mysql_real_escape_string($service_id);	$language_id = mysql_real_escape_string($language_id);	$Query = "SELECT * FROM language WHERE id = '" . $language_id . "' AND service_id = '" . $service_id . "'";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$language = $Database['language'];		$F = array();		$F['service_id'] = $service_id;		$F['id'] = $language_id;		$F['language'] = $language;				$ReturnObject = $F;		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>