<?php$route = '/services/:service_id/locations/:location_id/';$app->get($route, function ($service_id,$location_id)  use ($app){	$ReturnObject = array();	$service_id = mysql_real_escape_string($service_id);	$location_id = mysql_real_escape_string($location_id);	$Query = "SELECT * FROM location WHERE id = '" . $location_id . "' AND service_id = '" . $service_id . "'";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$location = $Database['location'];		$F = array();		$F['service_id'] = $service_id;		$F['id'] = $location_id;		$F['location'] = $location;				$ReturnObject = $F;		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>