<?php$route = '/services/:service_id/locations/';$app->post($route, function ($service_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$clean = filter_input_array(INPUT_POST,$_GET);	$service_id = mysql_real_escape_string($service_id);		if(isset($clean['location']))		{		$location_id = getGUID();				$location = $clean['location'];		$query = "INSERT INTO location(";				$query .= "service_id,";		$query .= "id,";		$query .= "location";		$query .= ") VALUES(";		$query .= "'" . mysql_real_escape_string($service_id) . "',";		$query .= "'" . mysql_real_escape_string($location_id) . "',";		$query .= "'" . mysql_real_escape_string($location) . "'";		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$F = array();		$F['service_id'] = $service_id;		$F['id'] = $location_id;		$F['location'] = $location;		$ReturnObject = $F;		}			$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>