<?php$route = '/services/:service_id/payment-accepted/';$app->get($route, function ($service_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$service_id = strip_tags(mysql_real_escape_string($service_id));	$ReturnObject = array();	$Query = "SELECT * FROM payment_accepted WHERE service_id = '" . $service_id . "'";	$Query .= " ORDER BY payment ASC";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$funding_id = $Database['id'];		$payment = $Database['payment'];		$F = array();			$F['id'] = $funding_id;		$F['service_id'] = $service_id;		$F['payment'] = $payment;					array_push($ReturnObject,$F);				}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));		});?>