<?php$route = '/services/:service_id/payment-accepted/:payment_accepted_id/';$app->get($route, function ($service_id,$funding_id)  use ($app){	$ReturnObject = array();	$service_id = mysql_real_escape_string($service_id);	$funding_id = mysql_real_escape_string($funding_id);	$Query = "SELECT * FROM payment_accepted WHERE id = '" . $funding_id . "' AND service_id = '" . $service_id . "'";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$payment = $Database['payment'];		$F = array();			$F['id'] = $funding_id;		$F['service_id'] = $service_id;		$F['payment'] = $payment;						$ReturnObject = $F;		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>