<?php$route = '/services/:service_id/payment-accepted/:payment_accepted_id/';$app->put($route, function ($service_id,$funding_id)  use ($app){	$request = $app->request();	$_GET = $request->params();			$service_id = mysql_real_escape_string($service_id);	$funding_id = mysql_real_escape_string($funding_id);	$payment = mysql_real_escape_string($_GET['payment']);	$query = "UPDATE payment_accepted SET ";	$query .= "service_id='" . mysql_real_escape_string($service_id) . "'";	$query .= ",id='" . mysql_real_escape_string($funding_id) . "'";	$query .= ",payment='" . mysql_real_escape_string($payment) . "'";	$query .= " WHERE id = '" . $funding_id . "'";	mysql_query($query) or die('Query failed: ' . mysql_error());	$F = array();		$F['id'] = $funding_id;	$F['service_id'] = $service_id;	$F['payment'] = $payment;			$ReturnObject = $F;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>