<?php$route = '/services/:service_id/payment-accepted/';$app->post($route, function ($service_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$clean = filter_input_array(INPUT_POST,$_GET);	$service_id = mysql_real_escape_string($service_id);		if(isset($clean['payment']))		{		$funding_id = getGUID();				$payment = $clean['payment'];		$query = "INSERT INTO payment_accepted(";					$query .= "id,";		$query .= "service_id,";		$query .= "payment";		$query .= ") VALUES(";				$query .= "'" . mysql_real_escape_string($funding_id) . "',";		$query .= "'" . mysql_real_escape_string($service_id) . "',";		$query .= "'" . mysql_real_escape_string($payment) . "'";		$query .= ")";		//echo $query;		mysql_query($query) or die('Query failed: ' . mysql_error());		$F = array();			$F['id'] = $funding_id;		$F['service_id'] = $service_id;		$F['payment'] = $payment;			$ReturnObject = $F;		}			$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>