<?php$route = '/services/:service_id/phones/:phone_id/';$app->put($route, function ($service_id,$phone_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$service_id = mysql_real_escape_string($service_id);	$phone_id = mysql_real_escape_string($phone_id);	$number = mysql_real_escape_string($_GET['number']);	$extension = mysql_real_escape_string($_GET['extension']);	$type = mysql_real_escape_string($_GET['type']);	$department = mysql_real_escape_string($_GET['department']);	$query = "UPDATE phone SET ";	$query .= "service_id='" . mysql_real_escape_string($service_id) . "'";	$query .= ",id='" . mysql_real_escape_string($phone_id) . "'";	$query .= ",number='" . mysql_real_escape_string($number) . "'";	$query .= ",extension='" . mysql_real_escape_string($extension) . "'";	$query .= ",type='" . mysql_real_escape_string($type) . "'";	$query .= ",department='" . mysql_real_escape_string($department) . "'";	$query .= " WHERE id = '" . $phone_id . "'";	//echo $query;	mysql_query($query) or die('Query failed: ' . mysql_error());	$F = array();	$F['service_id'] = $service_id;	$F['id'] = $phone_id;	$F['number'] = $number;	$F['extension'] = $extension;	$F['type'] = $type;	$F['department'] = $department;		$ReturnObject = $F;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>