<?php$route = '/services/:service_id/';$app->put($route, function ()  use ($app){		$ReturnObject = array();	$request = $app->request();	$_GET = $request->params();	$service_id = filter_var($_GET['id'], FILTER_SANITIZE_STRING);	$name = filter_var($_GET['name'], FILTER_SANITIZE_STRING);	$alternate_name = filter_var($_GET['alternate_name'], FILTER_SANITIZE_STRING);	$url = filter_var($_GET['url'], FILTER_SANITIZE_STRING);	$email = filter_var($_GET['email'], FILTER_SANITIZE_STRING);	$status = filter_var($_GET['status'], FILTER_SANITIZE_STRING);	$application_process = filter_var($_GET['application_process'], FILTER_SANITIZE_STRING);	$wait_time = filter_var($_GET['wait_time'], FILTER_SANITIZE_STRING);	$Query = "SELECT * FROM service WHERE id = '" . $service_id . "'";	//echo $Query;	$Database = mysql_query($Query) or die('Query failed: ' . mysql_error());	if($Database && mysql_num_rows($Database))		{		$query = "UPDATE service SET ";				$query .= "id='" . mysql_real_escape_string($service_id) . "'";		$query .= ",name='" . mysql_real_escape_string($name) . "'";		$query .= ",alternate_name='" . mysql_real_escape_string($alternate_name) . "'";		$query .= ",url='" . mysql_real_escape_string($url) . "'";		$query .= ",email='" . mysql_real_escape_string($email) . "'";		$query .= ",status='" . mysql_real_escape_string($status) . "'";		$query .= ",application_process='" . mysql_real_escape_string($application_process) . "'";		$query .= ",wait_time='" . mysql_real_escape_string($wait_time) . "'";		$query .= " WHERE id = '" . $service_id . "'";		//echo $query;		mysql_query($query) or die('Query failed: ' . mysql_error());		$F = array();		$F['id'] = $service_id;		$F['name'] = $name;		$F['alternate_name'] = $alternate_name;		$F['url'] = $url;		$F['email'] = $email;		$F['status'] = $status;		$F['application_process'] = $application_process;		$F['wait_time'] = $wait_time;				$ReturnObject = $F;		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>