<?php$route = '/services/:service_id/required-document/';$app->post($route, function ($service_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$clean = filter_input_array(INPUT_POST,$_GET);	$service_id = mysql_real_escape_string($service_id);		if(isset($clean['document']))		{		$required_document_id = getGUID();				$document = $clean['document'];		$query = "INSERT INTO required_document(";					$query .= "id,";		$query .= "service_id,";		$query .= "document";		$query .= ") VALUES(";				$query .= "'" . mysql_real_escape_string($required_document_id) . "',";		$query .= "'" . mysql_real_escape_string($service_id) . "',";		$query .= "'" . mysql_real_escape_string($document) . "'";		$query .= ")";		//echo $query;		mysql_query($query) or die('Query failed: ' . mysql_error());		$F = array();			$F['id'] = $required_document_id;		$F['service_id'] = $service_id;		$F['document'] = $document;			$ReturnObject = $F;		}			$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>