<?php$route = '/services/:service_id/required-document/:required_document_id/';$app->put($route, function ($service_id,$required_document_id)  use ($app){	$request = $app->request();	$_GET = $request->params();			$service_id = mysql_real_escape_string($service_id);	$required_document_id = mysql_real_escape_string($required_document_id);	$document = mysql_real_escape_string($_GET['document']);	$query = "UPDATE required_document SET ";	$query .= "service_id='" . mysql_real_escape_string($service_id) . "'";	$query .= ",id='" . mysql_real_escape_string($required_document_id) . "'";	$query .= ",document='" . mysql_real_escape_string($document) . "'";	$query .= " WHERE id = '" . $required_document_id . "'";	mysql_query($query) or die('Query failed: ' . mysql_error());	$F = array();		$F['id'] = $required_document_id;	$F['service_id'] = $service_id;	$F['document'] = $document;			$ReturnObject = $F;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>