<?php$route = '/services/:service_id/service-area/';$app->get($route, function ($service_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$service_id = strip_tags(mysql_real_escape_string($service_id));	$ReturnObject = array();	$Query = "SELECT * FROM service_area WHERE service_id = '" . $service_id . "'";	$Query .= " ORDER BY service_area ASC";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$service_area_id = $Database['id'];		$service_area = $Database['service_area'];		$F = array();			$F['id'] = $service_area_id;		$F['service_id'] = $service_id;		$F['service_area'] = $service_area;					array_push($ReturnObject,$F);				}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));		});?>