<?php$route = '/services/:service_id/service-area/';$app->post($route, function ($service_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$clean = filter_input_array(INPUT_POST,$_GET);	$service_id = mysql_real_escape_string($service_id);		if(isset($clean['service_area']))		{		$service_area_id = getGUID();				$service_area = $clean['service_area'];		$query = "INSERT INTO service_area(";					$query .= "id,";		$query .= "service_id,";		$query .= "service_area";		$query .= ") VALUES(";				$query .= "'" . mysql_real_escape_string($service_area_id) . "',";		$query .= "'" . mysql_real_escape_string($service_id) . "',";		$query .= "'" . mysql_real_escape_string($service_area) . "'";		$query .= ")";		//echo $query;		mysql_query($query) or die('Query failed: ' . mysql_error());		$F = array();			$F['id'] = $service_area_id;		$F['service_id'] = $service_id;		$F['service_area'] = $service_area;			$ReturnObject = $F;		}			$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>