<?php$route = '/services/:service_id/service-area/:service_area_id/';$app->get($route, function ($service_id,$service_area_id)  use ($app){	$ReturnObject = array();	$service_id = mysql_real_escape_string($service_id);	$service_area_id = mysql_real_escape_string($service_area_id);	$Query = "SELECT * FROM service_area WHERE id = '" . $service_area_id . "' AND service_id = '" . $service_id . "'";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$service_area = $Database['service_area'];		$F = array();			$F['id'] = $service_area_id;		$F['service_id'] = $service_id;		$F['service_area'] = $service_area;						$ReturnObject = $F;		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>