<?php$route = '/services/:service_id/service-area/:service_area_id/';$app->put($route, function ($service_id,$service_area_id)  use ($app){	$request = $app->request();	$_GET = $request->params();			$service_id = mysql_real_escape_string($service_id);	$service_area_id = mysql_real_escape_string($service_area_id);	$service_area = mysql_real_escape_string($_GET['service_area']);	$query = "UPDATE service_area SET ";	$query .= "service_id='" . mysql_real_escape_string($service_id) . "'";	$query .= ",id='" . mysql_real_escape_string($service_area_id) . "'";	$query .= ",service_area='" . mysql_real_escape_string($service_area) . "'";	$query .= " WHERE id = '" . $service_area_id . "'";	mysql_query($query) or die('Query failed: ' . mysql_error());	$F = array();		$F['id'] = $service_area_id;	$F['service_id'] = $service_id;	$F['service_area'] = $service_area;			$ReturnObject = $F;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>