<?php$route = '/organizations/:organization_id/contacts/:contact_id/';$app->delete($route, function ($organization_id,$contact_id)  use ($app){	$organization_id = filter_var($organization_id, FILTER_SANITIZE_STRING);	$contact_id = filter_var($contact_id, FILTER_SANITIZE_STRING);	$Query = "DELETE FROM contact WHERE id = '" . $contact_id . "' AND organization_id = '" . $organization_id . "'";	mysql_query($Query) or die('Query failed: ' . mysql_error());	$ReturnObject = array();	$ReturnObject['message'] = "Contact Deleted";	$ReturnObject['id'] = $contact_id;	$ReturnObject['organization_id'] = $organization_id;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>