<?php$route = '/organizations/:organization_id/contacts/';$app->get($route, function ($organization_id)  use ($app){	$organization_id = filter_var($organization_id, FILTER_SANITIZE_STRING);	$ReturnObject = array();	$Query = "SELECT * FROM contact WHERE organization_id = '" . $organization_id . "'";	$Query .= " ORDER BY name ASC";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$id = $Database['id'];		$organization_id = $Database['organization_id'];		$name = $Database['name'];		$title = $Database['title'];		$department = $Database['department'];		$email = $Database['email'];		$F = array();		$F['id'] = $id;		$F['organization_id'] = $organization_id;		$F['name'] = $name;		$F['title'] = $title;		$F['department'] = $department;		$F['email'] = $email;								array_push($ReturnObject,$F);				}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));		});?>