<?php$route = '/organizations/:organization_id/contacts/';$app->post($route, function ($organization_id)  use ($app){	$request = $app->request();	$_GET = $request->params();	$_BODY = $request->getBody();	$_BODY = json_decode($_BODY,true);		$clean = filter_var_array($_BODY,FILTER_SANITIZE_STRING);		$organization_id = filter_var($organization_id, FILTER_SANITIZE_STRING);		if(isset($clean['name']))		{		$id = getGUID();				$name = $clean['name'];		$title = $clean['title'];		$department = $clean['department'];		$email = $clean['email'];		$query = "INSERT INTO contact(";				$query .= "id,";		$query .= "organization_id,";		$query .= "name,";		$query .= "title,";		$query .= "department,";		$query .= "email";		$query .= ") VALUES(";		$query .= "'" . mysql_real_escape_string($id) . "',";		$query .= "'" . mysql_real_escape_string($organization_id) . "',";		$query .= "'" . mysql_real_escape_string($name) . "',";		$query .= "'" . mysql_real_escape_string($title) . "',";		$query .= "'" . mysql_real_escape_string($department) . "',";		$query .= "'" . mysql_real_escape_string($email) . "'";		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$F = array();		$F['id'] = $id;		$F['organization_id'] = $organization_id;		$F['name'] = $name;		$F['title'] = $title;		$F['department'] = $department;		$F['email'] = $email;		$ReturnObject = $F;		}			$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>