<?php$route = '/organizations/:organization_id/funding/:funding_id/';$app->delete($route, function ($organization_id,$funding_id)  use ($app){	$organization_id = filter_var($organization_id, FILTER_SANITIZE_STRING);	$funding_id = filter_var($funding_id, FILTER_SANITIZE_STRING);	$Query = "DELETE FROM funding WHERE id = '" . $funding_id . "' AND organization_id = '" . $organization_id . "'";	mysql_query($Query) or die('Query failed: ' . mysql_error());	$ReturnObject = array();	$ReturnObject['message'] = "Funding Deleted";	$ReturnObject['id'] = $funding_id;	$ReturnObject['organization_id'] = $organization_id;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>