<?php$route = '/organizations/:organization_id/funding/';$app->post($route, function ($organization_id)  use ($app){	$request = $app->request();	$_GET = $request->params();	$_BODY = $request->getBody();	$_BODY = json_decode($_BODY,true);			$clean = filter_var_array($_BODY,FILTER_SANITIZE_STRING);			$organization_id = filter_var($organization_id, FILTER_SANITIZE_STRING);		if(isset($clean['source']))		{		$funding_id = getGUID();				$source = $clean['source'];		$query = "INSERT INTO funding(";					$query .= "id,";		$query .= "organization_id,";		$query .= "source";		$query .= ") VALUES(";				$query .= "'" . mysql_real_escape_string($funding_id) . "',";		$query .= "'" . mysql_real_escape_string($organization_id) . "',";		$query .= "'" . mysql_real_escape_string($source) . "'";		$query .= ")";		//echo $query;		mysql_query($query) or die('Query failed: ' . mysql_error());		$F = array();			$F['id'] = $funding_id;		$F['organization_id'] = $organization_id;		$F['source'] = $source;			$ReturnObject = $F;		}			$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>