<?php$route = '/organizations/:organization_id/';$app->get($route, function ($organization_id)  use ($app){	$ReturnObject = array();	$organization_id = mysql_real_escape_string($organization_id);	$Query = "SELECT * FROM organization WHERE id = '" . $organization_id . "'";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$id = $Database['id'];		$name = $Database['name'];		$alternate_name = $Database['alternate_name'];		$description = $Database['description'];		$email = $Database['email'];		$url = $Database['url'];		$tax_status = $Database['tax_status'];		$tax_id = $Database['tax_id'];		$year_incorporated = $Database['year_incorporated'];		$legal_status = $Database['legal_status'];		$F = array();		$F['id'] = $id;		$F['name'] = $name;		$F['alternate_name'] = $alternate_name;		$F['description'] = $description;		$F['email'] = $email;		$F['url'] = $url;		$F['tax_status'] = $tax_status;		$F['tax_id'] = $tax_id;		$F['year_incorporated'] = $year_incorporated;		$F['legal_status'] = $legal_status;		$ReturnObject = $F;		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>