<?php$route = '/organizations/:organization_id/locations/:location_id/';$app->delete($route, function ($organization_id,$location_id)  use ($app){	$organization_id = filter_var($organization_id, FILTER_SANITIZE_STRING);	$location_id = filter_var($location_id, FILTER_SANITIZE_STRING);	$Query = "DELETE FROM location WHERE id = '" . $location_id . "' AND organization_id = '" . $organization_id . "'";	mysql_query($Query) or die('Query failed: ' . mysql_error());	$ReturnObject = array();	$ReturnObject['message'] = "Location Deleted";	$ReturnObject['id'] = $location_id;	$ReturnObject['organization_id'] = $organization_id;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>