<?php$route = '/organizations/:organization_id/locations/:location_id/';$app->put($route, function ($organization_id,$location_id)  use ($app){	$request = $app->request();	$_GET = $request->params();	$_BODY = $request->getBody();	$_BODY = json_decode($_BODY,true);			$organization_id = filter_var($organization_id, FILTER_SANITIZE_STRING);	$location_id = filter_var($location_id, FILTER_SANITIZE_STRING);		$name = filter_var($_BODY['name'], FILTER_SANITIZE_STRING);	$alternate_name = filter_var($_BODY['alternate_name'], FILTER_SANITIZE_STRING);	$transportation = filter_var($_BODY['transportation'], FILTER_SANITIZE_STRING);	$latitude = filter_var($_BODY['latitude'], FILTER_SANITIZE_STRING);	$longitude = filter_var($_BODY['longitude'], FILTER_SANITIZE_STRING);	$query = "UPDATE location SET ";	$query .= "organization_id='" . mysql_real_escape_string($organization_id) . "'";	$query .= ",name='" . mysql_real_escape_string($name) . "'";	$query .= ",alternate_name='" . mysql_real_escape_string($alternate_name) . "'";	$query .= ",transportation='" . mysql_real_escape_string($transportation) . "'";	$query .= ",latitude='" . mysql_real_escape_string($latitude) . "'";	$query .= ",longitude='" . mysql_real_escape_string($longitude) . "'";	$query .= " WHERE id = '" . $location_id . "'";	//echo $query;	mysql_query($query) or die('Query failed: ' . mysql_error());	$F = array();	$F['id'] = $location_id;	$F['organization_id'] = $organization_id;			$F['name'] = $name;	$F['alternate_name'] = $alternate_name;	$F['transportation'] = $transportation;	$F['latitude'] = $latitude;	$F['longitude'] = $longitude;		$ReturnObject = $F;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>