<?php$route = '/organizations/:organization_id/locations/';$app->post($route, function ($organization_id)  use ($app){	$request = $app->request();	$_GET = $request->params();	$_BODY = $request->getBody();	$_BODY = json_decode($_BODY,true);		$clean = filter_var_array($_BODY,FILTER_SANITIZE_STRING);		$organization_id = filter_var($organization_id, FILTER_SANITIZE_STRING);		if(isset($clean['name']))		{		$location_id = getGUID();				$name = $clean['name'];		$alternate_name = $clean['alternate_name'];		$transportation = $clean['transportation'];		$latitude = $clean['latitude'];		$longitude = $clean['longitude'];		$query = "INSERT INTO location(";				$query .= "organization_id,";		$query .= "id,";		$query .= "name,";		$query .= "alternate_name,";		$query .= "transportation,";		$query .= "latitude,";		$query .= "longitude";		$query .= ") VALUES(";		$query .= "'" . mysql_real_escape_string($organization_id) . "',";		$query .= "'" . mysql_real_escape_string($location_id) . "',";		$query .= "'" . mysql_real_escape_string($name) . "',";		$query .= "'" . mysql_real_escape_string($alternate_name) . "',";		$query .= "'" . mysql_real_escape_string($transportation) . "',";		$query .= "'" . mysql_real_escape_string($latitude) . "',";		$query .= "'" . mysql_real_escape_string($longitude) . "'";		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$F = array();		$F['id'] = $location_id;		$F['organization_id'] = $organization_id;				$F['name'] = $name;		$F['alternate_name'] = $alternate_name;		$F['transportation'] = $transportation;		$F['latitude'] = $latitude;		$F['longitude'] = $longitude;		$ReturnObject = $F;		}			$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>