<?php$route = '/organizations/:organization_id/phones/:phone_id/';$app->delete($route, function ($organization_id,$phone_id)  use ($app){	$organization_id = filter_var($organization_id, FILTER_SANITIZE_STRING);	$phone_id = filter_var($phone_id, FILTER_SANITIZE_STRING);	$Query = "DELETE FROM phone WHERE id = '" . $phone_id . "' AND organization_id = '" . $organization_id . "'";	mysql_query($Query) or die('Query failed: ' . mysql_error());	$ReturnObject = array();	$ReturnObject['message'] = "Phone Deleted";	$ReturnObject['id'] = $phone_id;	$ReturnObject['organization_id'] = $organization_id;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>