<?php$route = '/organizations/:organization_id/phones/:phone_id/';$app->put($route, function ($organization_id,$phone_id)  use ($app){	$request = $app->request();	$_GET = $request->params();	$_BODY = $request->getBody();	$_BODY = json_decode($_BODY,true);		$organization_id = filter_var($organization_id, FILTER_SANITIZE_STRING);		$phone_id = filter_var($phone_id, FILTER_SANITIZE_STRING);	$location_id = filter_var($_BODY['location_id'], FILTER_SANITIZE_STRING);		$service_id = filter_var($_BODY['service_id'], FILTER_SANITIZE_STRING);		$contact_id = filter_var($_BODY['contact_id'], FILTER_SANITIZE_STRING);		$service_at_location_id = filter_var($_BODY['service_at_location_id'], FILTER_SANITIZE_STRING);			$number = filter_var($_BODY['number'], FILTER_SANITIZE_STRING);		$extension = filter_var($_BODY['extension'], FILTER_SANITIZE_STRING);		$type = filter_var($_BODY['type'], FILTER_SANITIZE_STRING);		$department = filter_var($_BODY['department'], FILTER_SANITIZE_STRING);	$language = filter_var($_BODY['language'], FILTER_SANITIZE_STRING);		$description = filter_var($_BODY['description'], FILTER_SANITIZE_STRING);		$query = "UPDATE phone SET ";	$query .= "location_id='" . $location_id . "'";	$query .= ",service_id='" . $service_id . "'";	$query .= ",organization_id='" . $organization_id . "'";	$query .= ",contact_id='" . $contact_id . "'";	$query .= ",service_at_location_id='" . $service_at_location_id . "'";	$query .= ",number='" . $number . "'";	$query .= ",extension='" . $extension . "'";	$query .= ",type='" . $type . "'";	$query .= ",department='" . $department . "'";	$query .= ",language='" . $language . "'";	$query .= ",description='" . $description . "'";	$query .= " WHERE id = '" . $phone_id . "'";	//echo $query;	mysql_query($query) or die('Query failed: ' . mysql_error());	$F = array();	$F['id'] = $phone_id;	$F['location_id'] = $location_id;			$F['service_id'] = $service_id;	$F['organization_id'] = $organization_id;	$F['contact_id'] = $contact_id;	$F['service_at_location_id'] = $service_at_location_id;	$F['number'] = $number;	$F['extension'] = $extension;	$F['type'] = $type;	$F['department'] = $department;	$F['language'] = $language;	$F['description'] = $description;		$ReturnObject = $F;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>