<?php$route = '/organizations/:organization_id/programs/:program_id/';$app->delete($route, function ($organization_id,$program_id)  use ($app){	$organization_id = filter_var($organization_id, FILTER_SANITIZE_STRING);	$program_id = filter_var($program_id, FILTER_SANITIZE_STRING);	$Query = "DELETE FROM program WHERE id = '" . $program_id . "' AND organization_id = '" . $organization_id . "'";	mysql_query($Query) or die('Query failed: ' . mysql_error());	$ReturnObject = array();	$ReturnObject['message'] = "Program Deleted";	$ReturnObject['id'] = $program_id;	$ReturnObject['organization_id'] = $organization_id;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>