<?php$route = '/organizations/:organization_id/programs/:program_id/';$app->get($route, function ($organization_id,$program_id)  use ($app){	$ReturnObject = array();		$organization_id = filter_var($organization_id, FILTER_SANITIZE_STRING);	$program_id = filter_var($program_id, FILTER_SANITIZE_STRING);	$Query = "SELECT * FROM program WHERE id = '" . $program_id . "' AND organization_id = '" . $organization_id . "'";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$name = $Database['name'];		$alternate_name = $Database['alternate_name'];			$F = array();		$F['organization_id'] = $organization_id;		$F['id'] = $program_id;		$F['name'] = $name;		$F['alternate_name'] = $alternate_name;				$ReturnObject = $F;		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>