<?php$route = '/organizations/:organization_id/programs/:program_id/';$app->put($route, function ($organization_id,$program_id)  use ($app){	$request = $app->request();	$_GET = $request->params();	$_BODY = $request->getBody();	$_BODY = json_decode($_BODY,true);				$organization_id = filter_var($organization_id, FILTER_SANITIZE_STRING);	$program_id = filter_var($program_id, FILTER_SANITIZE_STRING);	$name = filter_var($_BODY['name'], FILTER_SANITIZE_STRING);	$alternate_name = filter_var($_BODY['alternate_name'], FILTER_SANITIZE_STRING);	$query = "UPDATE program SET ";	$query .= "organization_id='" . mysql_real_escape_string($organization_id) . "'";	$query .= ",name='" . mysql_real_escape_string($name) . "'";	$query .= ",alternate_name='" . mysql_real_escape_string($alternate_name) . "'";	$query .= " WHERE id = '" . $program_id . "'";	//echo $query;	mysql_query($query) or die('Query failed: ' . mysql_error());	$F = array();	$F['id'] = $program_id;	$F['organization_id'] = $organization_id;	$F['name'] = $name;	$F['alternate_name'] = $alternate_name;		$ReturnObject = $F;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>