<?php$route = '/organizations/:organization_id/programs/:program_id/services/';$app->get($route, function ($organization_id,$program_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$organization_id = filter_var($organization_id, FILTER_SANITIZE_STRING);	$program_id = filter_var($program_id, FILTER_SANITIZE_STRING);	$ReturnObject = array();	$Query = "SELECT * FROM service WHERE organization_id = '" . $organization_id . "'";	$Query .= " ORDER BY name ASC";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$id = $Database['id'];		$organization_id = $Database['organization_id'];		$program_id = $Database['program_id'];		$location_id = $Database['location_id'];		$name = $Database['name'];		$alternate_name = $Database['alternate_name'];		$url = $Database['url'];		$email = $Database['email'];		$status = $Database['status'];		$application_process = $Database['application_process'];		$wait_time = $Database['wait_time'];		$F = array();		$F['id'] = $id;		$F['organization_id'] = $organization_id;		$F['program_id'] = $program_id;		$F['location_id'] = $location_id;		$F['name'] = $name;		$F['alternate_name'] = $alternate_name;		$F['url'] = $url;		$F['email'] = $email;		$F['status'] = $status;		$F['application_process'] = $application_process;		$F['wait_time'] = $wait_time;				array_push($ReturnObject,$F);				}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));		});?>