<?php$route = '/organizations/:organization_id/';$app->put($route, function ($organization_id)  use ($app){	$request = $app->request();	$_BODY = $request->getBody();	$_BODY = json_decode($_BODY,true);		$organization_id = filter_var($organization_id);		$name = filter_var($_BODY['name'], FILTER_SANITIZE_STRING);	$alternate_name = filter_var($_BODY['alternate_name'], FILTER_SANITIZE_STRING);	$description = filter_var($_BODY['description'], FILTER_SANITIZE_STRING);	$email = filter_var($_BODY['email'], FILTER_SANITIZE_STRING);	$url = filter_var($_BODY['url'], FILTER_SANITIZE_STRING);	$tax_status = filter_var($_BODY['tax_status'], FILTER_SANITIZE_STRING);	$tax_id = filter_var($_BODY['tax_id'], FILTER_SANITIZE_STRING);	$year_incorporated = filter_var($_BODY['year_incorporated'], FILTER_SANITIZE_STRING);	$legal_status = filter_var($_BODY['legal_status'], FILTER_SANITIZE_STRING);	$Query = "SELECT * FROM organization WHERE id = '" . $organization_id . "'";	//echo $Query;	$Database = mysql_query($Query) or die('Query failed: ' . mysql_error());	if($Database && mysql_num_rows($Database))		{		$query = "UPDATE organization SET ";		$query .= "name='" . $name . "'";		$query .= ",alternate_name='" . $alternate_name . "'";		$query .= ",description='" . $description . "'";		$query .= ",email='" . $email . "'";		$query .= ",url='" . $url . "'";		$query .= ",tax_status='" . $tax_status . "'";		$query .= ",tax_id='" . $tax_id . "'";		$query .= ",year_incorporated='" . $year_incorporated . "'";		$query .= ",legal_status='" . $legal_status . "'";		$query .= " WHERE id = '" . $organization_id . "'";		//echo $query;		mysql_query($query) or die('Query failed: ' . mysql_error());		}	$F = array();	$F['id'] = $organization_id;	$F['name'] = $name;	$F['alternate_name'] = $alternate_name;	$F['description'] = $description;	$F['email'] = $email;	$F['url'] = $url;	$F['tax_status'] = $tax_status;	$F['tax_id'] = $tax_id;	$F['year_incorporated'] = $year_incorporated;	$F['legal_status'] = $legal_status;		$ReturnObject = $F;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>