<?php$route = '/organizations/:organization_id/services/';$app->get($route, function ($organization_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$organization_id = filter_var($organization_id, FILTER_SANITIZE_STRING);	$ReturnObject = array();	$Query = "SELECT * FROM service WHERE organization_id = '" . $organization_id . "'";	$Query .= " ORDER BY name ASC";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$service_id = $Database['id'];			$name = $Database['name'];		$alternate_name = $Database['alternate_name'];			$F = array();		$F['id'] = $service_id;		$F['organization_id'] = $organization_id;		$F['name'] = $name;		$F['alternate_name'] = $alternate_name;				array_push($ReturnObject,$F);				}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));		});?>