<?php$route = '/organizations/:organization_id/services/';$app->post($route, function ($organization_id)  use ($app){	$request = $app->request();	$_GET = $request->params();	$_BODY = $request->getBody();	$_BODY = json_decode($_BODY,true);		$clean = filter_var_array($_BODY,FILTER_SANITIZE_STRING);			$organization_id = filter_var($organization_id, FILTER_SANITIZE_STRING);		if(isset($clean['name']))		{		$service_id = getGUID();				$name = $clean['name'];		$alternate_name = $clean['alternate_name'];			$query = "INSERT INTO service(";			$query .= "organization_id,";		$query .= "id,";		$query .= "name,";		$query .= "alternate_name";		$query .= ") VALUES(";		$query .= "'" . mysql_real_escape_string($organization_id) . "',";		$query .= "'" . mysql_real_escape_string($service_id) . "',";		$query .= "'" . mysql_real_escape_string($name) . "',";		$query .= "'" . mysql_real_escape_string($alternate_name) . "'";		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$F = array();		$F['id'] = $service_id;		$F['organization_id'] = $organization_id;		$F['name'] = $name;		$F['alternate_name'] = $alternate_name;		$ReturnObject = $F;		}			$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>