<?php$route = '/services/:service_id/contacts/:contact_id/';$app->delete($route, function ($service_id,$contact_id)  use ($app){	$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);	$contact_id = filter_var($contact_id, FILTER_SANITIZE_STRING);	$Query = "DELETE FROM contact WHERE id = '" . $contact_id . "' AND service_id = '" . $service_id . "'";	mysql_query($Query) or die('Query failed: ' . mysql_error());	$ReturnObject = array();	$ReturnObject['message'] = "Contact Deleted!";	$ReturnObject['id'] = $contact_id;	$ReturnObject['service_id'] = $service_id;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>