<?php$route = '/services/:service_id/contacts/:contact_id/';$app->put($route, function ($service_id,$contact_id)  use ($app){	$request = $app->request();	$_GET = $request->params();	$_BODY = $request->getBody();	$_BODY = json_decode($_BODY,true);			$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);	$contact_id = filter_var($contact_id, FILTER_SANITIZE_STRING);		$organization_id = filter_var($_BODY['organization_id'], FILTER_SANITIZE_STRING);	$service_at_location_id = filter_var($_BODY['service_at_location_id'], FILTER_SANITIZE_STRING);	$name = filter_var($_BODY['name'], FILTER_SANITIZE_STRING);	$title = filter_var($_BODY['title'], FILTER_SANITIZE_STRING);	$department = filter_var($_BODY['department'], FILTER_SANITIZE_STRING);	$email = filter_var($_BODY['email'], FILTER_SANITIZE_STRING);	$query = "UPDATE contact SET ";	$query .= "organization_id='" . mysql_real_escape_string($organization_id) . "'";	$query .= ",service_id='" . mysql_real_escape_string($service_id) . "'";	$query .= ",service_at_location_id='" . mysql_real_escape_string($service_at_location_id) . "'";		$query .= ",name='" . mysql_real_escape_string($name) . "'";	$query .= ",title='" . mysql_real_escape_string($title) . "'";	$query .= ",department='" . mysql_real_escape_string($department) . "'";	$query .= ",email='" . mysql_real_escape_string($email) . "'";	$query .= " WHERE id = '" . $contact_id . "'";	mysql_query($query) or die('Query failed: ' . mysql_error());	$F = array();	$F['id'] = $contact_id;	$F['organization_id'] = $organization_id;	$F['service_id'] = $service_id;	$F['service_at_location_id'] = $service_at_location_id;	$F['name'] = $name;	$F['title'] = $title;	$F['department'] = $department;	$F['email'] = $email;		$ReturnObject = $F;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>