<?php$route = '/services/:service_id/';$app->delete($route, function ($service_id)  use ($app){	$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);		$Query = "DELETE FROM service WHERE id = '" . $service_id . "'";	mysql_query($Query) or die('Query failed: ' . mysql_error());	$ReturnObject = array();	$ReturnObject['message'] = "Service Deleted!";	$ReturnObject['service_id'] = $service_id;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>