<?php$route = '/services/:service_id/eligibility/:eligibility_id/';$app->delete($route, function ($service_id,$eligibility_id)  use ($app){	$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);	$eligibility_id = filter_var($eligibility_id, FILTER_SANITIZE_STRING);	$Query = "DELETE FROM eligibility WHERE id = '" . $eligibility_id . "' AND service_id = '" . $service_id . "'";	mysql_query($Query) or die('Query failed: ' . mysql_error());	$ReturnObject = array();	$ReturnObject['message'] = "Eligibility Deleted!";	$ReturnObject['id'] = $eligibility_id;	$ReturnObject['service_id'] = $service_id;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>