<?php$route = '/services/:service_id/eligibility/:eligibility_id/';$app->get($route, function ($service_id,$eligibility_id)  use ($app){	$ReturnObject = array();	$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);	$eligibility_id = filter_var($eligibility_id, FILTER_SANITIZE_STRING);	$Query = "SELECT * FROM eligibility WHERE id = '" . $eligibility_id . "' AND service_id = '" . $service_id . "'";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$eligibility = $Database['eligibility'];		$F = array();			$F['id'] = $eligibility_id;		$F['service_id'] = $service_id;		$F['eligibility'] = $eligibility;						$ReturnObject = $F;		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>