<?php$route = '/services/:service_id/eligibility/:eligibility_id/';$app->put($route, function ($service_id,$eligibility_id)  use ($app){	$request = $app->request();	$_GET = $request->params();	$_BODY = $request->getBody();	$_BODY = json_decode($_BODY,true);				$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);	$eligibility_id = filter_var($eligibility_id, FILTER_SANITIZE_STRING);		$eligibility = filter_var($_BODY['eligibility'], FILTER_SANITIZE_STRING);	$query = "UPDATE eligibility SET ";	$query .= "service_id='" . mysql_real_escape_string($service_id) . "'";	$query .= ",id='" . mysql_real_escape_string($eligibility_id) . "'";	$query .= ",eligibility='" . mysql_real_escape_string($eligibility) . "'";	$query .= " WHERE id = '" . $eligibility_id . "'";	mysql_query($query) or die('Query failed: ' . mysql_error());	$F = array();		$F['id'] = $eligibility_id;	$F['service_id'] = $service_id;	$F['eligibility'] = $eligibility;			$ReturnObject = $F;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>