<?php$route = '/services/:service_id/eligibility/';$app->get($route, function ($service_id)  use ($app){	$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);	$ReturnObject = array();	$Query = "SELECT * FROM eligibility WHERE service_id = '" . $service_id . "'";	$Query .= " ORDER BY eligibility ASC";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$eligibility_id = $Database['id'];		$source = $Database['source'];		$F = array();			$F['id'] = $eligibility_id;		$F['service_id'] = $service_id;		$F['source'] = $source;					array_push($ReturnObject,$F);				}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));		});?>