<?php$route = '/services/:service_id/fees/:feed_id/';$app->delete($route, function ($service_id,$fee_id)  use ($app){	$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);	$fee_id = filter_var($fee_id, FILTER_SANITIZE_STRING);	$Query = "DELETE FROM fee WHERE id = '" . $fee_id . "' AND service_id = '" . $service_id . "'";	mysql_query($Query) or die('Query failed: ' . mysql_error());	$ReturnObject = array();	$ReturnObject['message'] = "Fee Deleted";	$ReturnObject['id'] = $fee_id;	$ReturnObject['service_id'] = $service_id;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>