<?php$route = '/services/:service_id/fees/:feed_id/';$app->put($route, function ($service_id,$fee_id)  use ($app){	$request = $app->request();	$_BODY = $request->getBody();	$_BODY = json_decode($_BODY,true);		$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);	$fee_id = filter_var($fee_id, FILTER_SANITIZE_STRING);	$fee = filter_var($_BODY['fee'], FILTER_SANITIZE_STRING);	$Query = "SELECT * FROM fee WHERE id = '" . $fee_id . "'";	$Database = mysql_query($Query) or die('Query failed: ' . mysql_error());	if($Database && mysql_num_rows($Database))		{		$query = "UPDATE fee SET";		if(isset($service_id))			{			$query .= " service_id='" . mysql_real_escape_string($service_id) . "'";			}		if(isset($feed_id))			{			$query .= ",feed_id='" . mysql_real_escape_string($feed_id) . "'";			}		if(isset($fee))			{			$query .= ",fee='" . mysql_real_escape_string($fee) . "'";			}		$query .= " WHERE id = '" . $fee_id . "'";		mysql_query($query) or die('Query failed: ' . mysql_error());		$F = array();			$F['id'] = $fee_id;		$F['service_id'] = $service_id;		$F['fee'] = $fee;				$ReturnObject = $F;		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>